  `article_id` bigint NOT NULL,
  `content` varchar(1500) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '留言的内容',
  `status` int UNSIGNED NULL DEFAULT 1 COMMENT '当前留言的状态 2已屏蔽 1正常 0审核中',
