  `graduated` int NULL DEFAULT NULL COMMENT '1代表研究生\r\n0代表本科生',
  `enro_year` int NULL DEFAULT NULL COMMENT '入学年份',
  `nick` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '昵称',
  `user_province` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `user_city` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `user_point` int NULL DEFAULT NULL COMMENT '用户积分',
  `remaining_view_num` int NULL DEFAULT NULL COMMENT '用户剩余浏览量',
  `user_exp` int NULL DEFAULT NULL COMMENT '用户经验',
  `avatar_url` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `signature` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
